import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Img1 from './carousel-images/image1.png';
import Img2 from './carousel-images/image2.png';
import Img3 from './carousel-images/image3.png';
import Img4 from './carousel-images/image4.png';

const CarouselMain = () => {
  const navigate = useNavigate();
  const imgCon = [Img1, Img2, Img3, Img4];
  const [ind, setInd] = useState(0);
  const indRef = useRef(0); // avoid stale closure in setInterval

  useEffect(() => {
    const interval = setInterval(() => {
      indRef.current = (indRef.current + 1) % imgCon.length;
      setInd(indRef.current);
    }, 3000);
    return () => clearInterval(interval);
  }, [imgCon.length]);

  const handlePrev = () => {
    const newIndex = (ind - 1 + imgCon.length) % imgCon.length;
    indRef.current = newIndex;
    setInd(newIndex);
  };

  const handleNext = () => {
    const newIndex = (ind + 1) % imgCon.length;
    indRef.current = newIndex;
    setInd(newIndex);
  };

  return (
    <div className="container text-center mt-4">
      <div className="alert alert-success fs-3 fw-bold" role="alert">
        Welcome to Resume Builder
      </div>

      <div className="position-relative">
        <img
          src={imgCon[ind]}
          alt={`carousel-img-${ind}`}
          className="img-fluid rounded shadow"
          style={{ height: '70vh', objectFit: 'contain' }}
        />

        <button
          className="btn btn-dark position-absolute top-50 start-0 translate-middle-y"
          onClick={handlePrev}
        >
          ‹
        </button>

        <button
          className="btn btn-dark position-absolute top-50 end-0 translate-middle-y"
          onClick={handleNext}
        >
          ›
        </button>
      </div>

      <button
        className="btn btn-success mt-4 px-4 py-2 fw-bold"
        onClick={() => navigate('/create-resume')}
      >
        Create Your CV
      </button>
    </div>
  );
};

export default CarouselMain;
