import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const CreateResume = ({ setResume }) => {
  const navigate = useNavigate();
  const [resumeValues, setResumeValues] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    emailId: '',
    contactNumber: '',
    address: '',
    city: '',
    state: '',
    pincode: '',
    nationality: '',
    heading: {
      heading: '',
      summary: ''
    },
    education: [{
      course: '',
      college: '',
      board: '',
      percentage: '',
      yearOfPass: ''
    }],
    experience: [{
      organisation: '',
      designation: '',
      from: '',
      to: ''
    }],
    skills: [{
      skillName: ''
    }]
  });

  // General handler for top-level fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setResumeValues(prev => ({ ...prev, [name]: value }));
  };

  // For nested heading object
  const handleHeadingChange = (e) => {
    const { name, value } = e.target;
    setResumeValues(prev => ({
      ...prev,
      heading: {
        ...prev.heading,
        [name]: value
      }
    }));
  };

  const handleChangeEducation = (e, index, field) => {
    const updated = [...resumeValues.education];
    updated[index][field] = e.target.value;
    setResumeValues(prev => ({ ...prev, education: updated }));
  };

  const handleChangeExperience = (e, index, field) => {
    const updated = [...resumeValues.experience];
    updated[index][field] = e.target.value;
    setResumeValues(prev => ({ ...prev, experience: updated }));
  };

  const handleChangeSkill = (e, index) => {
    const updated = [...resumeValues.skills];
    updated[index].skillName = e.target.value;
    setResumeValues(prev => ({ ...prev, skills: updated }));
  };

  const handleAddEducation = (e) => {
    e.preventDefault();
    setResumeValues(prev => ({
      ...prev,
      education: [...prev.education, { course: '', college: '', board: '', percentage: '', yearOfPass: '' }]
    }));
  };

  const handleDeleteEducation = (e, i) => {
    e.preventDefault();
    setResumeValues(prev => ({
      ...prev,
      education: prev.education.filter((_, index) => index !== i)
    }));
  };

  const handleAddExperience = (e) => {
    e.preventDefault();
    setResumeValues(prev => ({
      ...prev,
      experience: [...prev.experience, { organisation: '', designation: '', from: '', to: '' }]
    }));
  };

  const handleDeleteExperience = (e, i) => {
    e.preventDefault();
    setResumeValues(prev => ({
      ...prev,
      experience: prev.experience.filter((_, index) => index !== i)
    }));
  };

  const handleAddSkill = (e) => {
    e.preventDefault();
    setResumeValues(prev => ({
      ...prev,
      skills: [...prev.skills, { skillName: '' }]
    }));
  };

  const handleDeleteSkill = (e, i) => {
    e.preventDefault();
    setResumeValues(prev => ({
      ...prev,
      skills: prev.skills.filter((_, index) => index !== i)
    }));
  };

  const handlePreviewResume = (e) => {
    e.preventDefault();
    setResume(resumeValues);
    navigate('/resume-preview');
  };

  return (
    <form>
      <div className='createResume'>
        <div className='createInput'>
          <div className='head'><h2>Enter Details Given Below</h2></div>
          <div className='heading'><h3>Contact Information</h3></div>

          <div className='inpMedia'>
            <div className='media'><input className='mediainp' name="firstName" value={resumeValues.firstName} onChange={handleChange} type='text' placeholder='First Name' required /><span>First Name</span></div>
            <div className='media'><input className='mediainp' name="middleName" value={resumeValues.middleName} onChange={handleChange} type='text' placeholder='Middle Name' /><span>Middle Name</span></div>
            <div className='media'><input className='mediainp' name="lastName" value={resumeValues.lastName} onChange={handleChange} type='text' placeholder='Last Name' required /><span>Last Name</span></div>
          </div>

          <div className='inpMedia'>
            <div className='media'><input className='mediainp tele' name="emailId" value={resumeValues.emailId} onChange={handleChange} type='email' placeholder='Email id' required /><span>Email Id</span></div>
            <div className='media'><input className='mediainp tele' name="contactNumber" value={resumeValues.contactNumber} onChange={handleChange} type='text' placeholder='Contact Number' required /><span>Contact Number</span></div>
          </div>

          <div className='inpMedia'><div className='media'><input className='mediainp summary' name="address" value={resumeValues.address} onChange={handleChange} type='text' placeholder='Address' required /><span>Address</span></div></div>
          <div className='inpMedia'><div className='media'><input className='mediainp summary' name="city" value={resumeValues.city} onChange={handleChange} type='text' placeholder='City' required /><span>City</span></div></div>
          <div className='inpMedia'><div className='media'><input className='mediainp summary' name="state" value={resumeValues.state} onChange={handleChange} type='text' placeholder='State' required /><span>State</span></div></div>

          <div className='inpMedia'>
            <div className='media'><input className='mediainp tele' name="pincode" value={resumeValues.pincode} onChange={handleChange} type='text' placeholder='Pincode' required /><span>Pincode</span></div>
            <div className='media'><input className='mediainp tele' name="nationality" value={resumeValues.nationality} onChange={handleChange} type='text' placeholder='Nationality' required /><span>Nationality</span></div>
          </div>
          
          <div className='heading headingAndSummary'><h3>Resume Heading and Summary</h3></div>
          <div className='inpMedia'>
            <div className='media'><input className='mediainp summary' name="heading" value={resumeValues.heading.heading} onChange={handleHeadingChange} type='text' placeholder='Heading' required /><span>Heading</span></div>
          </div>
          <div className='inpMedia'>
            <div className='media'><input className='mediainp summary' name="summary" value={resumeValues.heading.summary} onChange={handleHeadingChange} type='text' placeholder='Summary' required /><span>Summary</span></div>
          </div>

          {/* EDUCATION */}
          <div className='heading'><h3>Education / Qualification</h3><button className='btn-add' onClick={handleAddEducation}>Add Education</button></div>
          {resumeValues.education.map((edu, i) => (
            <div className='division' key={i}>
              {resumeValues.education.length > 1 && <button className='btn-delete' onClick={(e) => handleDeleteEducation(e, i)}>delete</button>}
              <div className='inpMedia'>
                <div className='media'><input className='mediainp summary' value={edu.course} onChange={(e) => handleChangeEducation(e, i, 'course')} type='text' placeholder='Degree / Course' /><span>Course</span></div>
              </div>
              <div className='inpMedia'>
                <div className='media'><input className='mediainp summary' value={edu.college} onChange={(e) => handleChangeEducation(e, i, 'college')} type='text' placeholder='College' /><span>College</span></div>
              </div>
              <div className='inpMedia'>
                <div className='media'><input className='mediainp summary' value={edu.board} onChange={(e) => handleChangeEducation(e, i, 'board')} type='text' placeholder='Board' /><span>Board</span></div>
              </div>
              <div className='inpMedia'>
                <div className='media'><input className='mediainp tele' value={edu.percentage} onChange={(e) => handleChangeEducation(e, i, 'percentage')} type='text' placeholder='Percentage' /><span>Percentage</span></div>
                <div className='media'><input className='mediainp tele' value={edu.yearOfPass} onChange={(e) => handleChangeEducation(e, i, 'yearOfPass')} type='text' placeholder='Year' /><span>Year</span></div>
              </div>
            </div>
          ))}

          {/* EXPERIENCE */}
          <div className='heading'><h3>Work Experience</h3><button className='btn-add' onClick={handleAddExperience}>Add Experience</button></div>
          {resumeValues.experience.map((exp, i) => (
            <div className='division' key={i}>
              {resumeValues.experience.length > 1 && <button className='btn-delete' onClick={(e) => handleDeleteExperience(e, i)}>delete</button>}
              <div className='inpMedia'><div className='media'><input className='mediainp summary' value={exp.organisation} onChange={(e) => handleChangeExperience(e, i, 'organisation')} type='text' placeholder='Organisation' /><span>Organisation</span></div></div>
              <div className='inpMedia'><div className='media'><input className='mediainp summary' value={exp.designation} onChange={(e) => handleChangeExperience(e, i, 'designation')} type='text' placeholder='Designation' /><span>Designation</span></div></div>
              <div className='inpMedia'>
                <div className='media'><input className='mediainp tele' value={exp.from} onChange={(e) => handleChangeExperience(e, i, 'from')} type='text' placeholder='From' /><span>From</span></div>
                <div className='media'><input className='mediainp tele' value={exp.to} onChange={(e) => handleChangeExperience(e, i, 'to')} type='text' placeholder='To' /><span>To</span></div>
              </div>
            </div>
          ))}

          {/* SKILLS */}
          <div className='heading'><h3>Skills</h3><button className='btn-add' onClick={handleAddSkill}>Add Skill</button></div>
          <div className='division skills'>
            {resumeValues.skills.map((ele, i) => (
              <div className='inpMedia' key={i}>
                {resumeValues.skills.length > 1 && <button className='btn-delete' onClick={(e) => handleDeleteSkill(e, i)}>delete</button>}
                <div className='media'><input className='mediainp' value={ele.skillName} onChange={(e) => handleChangeSkill(e, i)} type='text' placeholder='Skill' /><span>Skill</span></div>
              </div>
            ))}
          </div>

          <button onClick={handlePreviewResume}>Preview Resume</button>
        </div>
      </div>
    </form>
  );
};

export default CreateResume;
