import { useRef } from 'react';
import html2pdf from 'html2pdf.js';

const ResumePreview = ({ resumeValues }) => {
  const resumeRef = useRef();

  const handleDownloadPDF = () => {
    const element = resumeRef.current;
    html2pdf()
      .set({
        margin: 0.5,
        filename: 'Resume.pdf',
        image: { type: 'jpeg', quality: 0.98 },
        html2canvas: { scale: 2 },
        jsPDF: { unit: 'in', format: 'a4', orientation: 'portrait' }
      })
      .from(element)
      .save();
  };

  return (
    <div>
      {/* Resume Preview */}
      <div ref={resumeRef} className="resume-preview">
        <div className="resume-container">
          <header>
            <h1 id="name">{resumeValues.firstName} {resumeValues.middleName} {resumeValues.lastName}</h1>
            <p id="contact">Email: {resumeValues.emailId} | Phone: {resumeValues.contactNumber}</p>
            <p id="address">{resumeValues.city}, {resumeValues.state} - {resumeValues.pincode} | Nationality: {resumeValues.nationality}</p>
            <p>{resumeValues.address}</p>
          </header>

          <section className="section">
            <h2>Professional Summary</h2>
            <h3 id="heading-title">{resumeValues.heading.heading}</h3>
            <p id="summary">{resumeValues.heading.summary}</p>
          </section>

          <section className="section">
            <h2>Education</h2>
            {resumeValues.education.map((edu, i) => (
              <div className="education-entry" key={i}>
                <h3>{edu.course}</h3>
                <p>{edu.college} - {edu.board}</p>
                <p>Percentage: {edu.percentage} | Year: {edu.yearOfPass}</p>
              </div>
            ))}
          </section>

          <section className="section">
            <h2>Experience</h2>
            {resumeValues.experience.map((exp, i) => (
              <div className="experience-entry" key={i}>
                <h3>{exp.designation} at {exp.organisation}</h3>
                <p>From: {exp.from} – To: {exp.to}</p>
              </div>
            ))}
          </section>

          <section className="section">
            <h2>Skills</h2>
            <ul className="skills-list">
              {resumeValues.skills.map((s, i) => (
                <li key={i}>{s.skillName}</li>
              ))}
            </ul>
          </section>
        </div>
      </div>

      <button onClick={handleDownloadPDF}>Download as PDF</button>
    </div>
  );
};

export default ResumePreview;
