import { useRef } from 'react';
import html2pdf from 'html2pdf.js';

const ResumePreview = ({resumeValues}) => {
  const resumeRef = useRef();

  const handleDownloadPDF = () => {
    const element = resumeRef.current;
    html2pdf()
      .set({ 
        margin: 0.5,
        filename: 'Resume.pdf',
        image: { type: 'jpeg', quality: 0.98 },
        html2canvas: { scale: 2 },
        jsPDF: { unit: 'in', format: 'a4', orientation: 'portrait' }
      })
      .from(element)
      .save();
  };

  return (
    <div>
      {/* Your form here to input resumeValues */}

      {/* Resume Preview */}
      <div ref={resumeRef} className="resume-preview">
        <div class="resume-container">
            <header>
            <h1 id="name">First Middle Last</h1>
            <p id="contact">Email: someone@example.com | Phone: 1234567890</p>
            <p id="address">City, State - Pincode | Nationality: Indian</p>
            </header>

            <section class="section">
            <h2>Professional Summary</h2>
            <h3 id="heading-title">Your Job Title</h3>
            <p id="summary">A short summary about your experience, skills, and goals.</p>
            </section>

            <section class="section">
            <h2>Education</h2>
            <div class="education-entry">
                <h3>Course Name</h3>
                <p>College Name - Board</p>
                <p>Percentage: 90% | Year: 2024</p>
            </div>
            </section>

            <section class="section">
            <h2>Experience</h2>
            <div class="experience-entry">
                <h3>Designation at Organisation</h3>
                <p>From: Jan 2023 â€“ To: May 2024</p>
            </div>
            </section>

            <section class="section">
            <h2>Skills</h2>
            <ul class="skills-list">
                <li>JavaScript</li>
                <li>React</li>
                <li>Python</li>
            </ul>
            </section>
        </div>
      </div>

      <button onClick={handleDownloadPDF}>Download as PDF</button>
    </div>
  );
};

export default ResumePreview
